import React, {useState} from 'react';
import {Container, Dropdown, DropdownMenu, DropdownToggle, Form} from "react-bootstrap";

const CreateOrder = () => {
    const [value, setValue] = useState('');
    const types = [
        {id: 1, name: "type num 1"},
        {id: 2, name: "type num 2"},
    ]

    const handleChange = (e) => {
        const newValue = e.target.value;
        if (newValue === '' || Number(newValue) >= 0) {
            setValue(newValue);
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === '-') {
            e.preventDefault();
        }
    };

    return (
        <Container className="mt-5" style={{width:600}}>
            <Form>
                <Dropdown>
                    <DropdownToggle>Chose type</DropdownToggle>
                    <DropdownMenu>{types.map(type => (
                        <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
                    ))}</DropdownMenu>
                </Dropdown>
                <Form.Control className={"mt-3"} placeholder={"Enter name of order..."}/>
                <Form.Control className={"mt-3"} placeholder={"Enter little description of order..."}/>
                <Form.Control className={"mt-3"} placeholder={"Enter full description of order..."}/>
                <Form.Control  value={value} onChange={handleChange} onKeyPress={handleKeyPress} type={'number'} className={"mt-3"} placeholder={"Enter order price..."}/>
            </Form>
        </ Container>
    )
};

export default CreateOrder;
